    private boolean notEmpty(String s){
        return (!s.isEmpty());
    }
    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        String s="";
        if(notEmpty(jTextField1.getText())){
            jTextArea2.append("User "+jTextField1.getText()+" information\n");
        }
        if(notEmpty(jPasswordField1.getText())){
            for(int i=0;i<jTextField1.getText().length();i++)
                s+="*";
            jTextArea2.append("Password: "+s+"\n");
        }
        s="Gender: ";
        if(jRadioButton1.isSelected()){
            s+=jRadioButton1.getText();
            jTextArea2.append(s+"\n");
        }else if(jRadioButton2.isSelected()){
            s+=jRadioButton2.getText();
            jTextArea2.append(s+"\n");
        }
        s="Position: ";
        if(jCheckBox1.isSelected())
            s+=jCheckBox1.getText()+", ";
        if(jCheckBox2.isSelected())
            s+=jCheckBox2.getText()+", ";
        if(jCheckBox3.isSelected())
            s+=jCheckBox3.getText();
        jTextArea2.append(s+"\n");
        jTextArea2.append("Address: "+jComboBox1.getSelectedItem().toString()+"\n");
        if(notEmpty(jTextArea1.getText())){
            jTextArea2.append("Hobby: "+jTextArea1.getText());
        }
        
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        if(JOptionPane.showConfirmDialog(this, "Do you want to cancel?", "Cancel?", JOptionPane.YES_NO_OPTION)==JOptionPane.YES_OPTION)
            System.exit(0);
    }//GEN-LAST:event_jButton3ActionPerformed

